module testbench;
    reg clk, reset, data;
    wire start_shifting;

    // Instantiate the Device Under Test (DUT)
    top_module dut(
        .clk(clk),
        .reset(reset),
        .data(data),
        .start_shifting(start_shifting)
    );

    // Clock generation
    always #5 clk = ~clk; // Generate a clock with period 10ns

    // Test cases
    initial begin
        // Initialize variables
        clk = 0;
        reset = 1; // Assert reset
        data = 0;
        #10; // Wait for the reset to take effect

        reset = 0; // De-assert reset
        $display("Starting test...");

        // Test sequence 1: Correct sequence 1101
        data = 1; #10;
        data = 1; #10;
        data = 0; #10;
        data = 1; #10;
        if (start_shifting !== 1) begin
            $display("Test Case Failed: Sequence 1101 did not set start_shifting high.");
            $finish;
        end

        // Test sequence 2: Check that it holds high
        data = 0; #10;
        if (start_shifting !== 1) begin
            $display("Test Case Failed: start_shifting did not hold high after sequence.");
            $finish;
        end

        // Test sequence 3: Reset and check response
        reset = 1; #10;
        reset = 0; #10;
        if (start_shifting !== 0) begin
            $display("Test Case Failed: start_shifting did not reset to 0.");
            $finish;
        end

        // Test sequence 4: Incorrect sequences before the correct one
        data = 0; #10;
        data = 1; #10;
        data = 1; #10;
        data = 0; #10;
        data = 1; #10; // Now the sequence 1101 occurs
        if (start_shifting !== 1) begin
            $display("Test Case Failed: start_shifting did not get set high after delayed correct sequence.");
            $finish;
        end

        // All tests passed
        $display("all test cases passed.");
    end
endmodule

