module testbench;

  reg [4:1] x;
  wire f;
  integer failures = 0;

  top_module DUT (
    .x(x),
    .f(f)
  );

  // Function to apply test vectors and check output
  task apply_test;
    input [4:1] test_vector;
    input expected_output;
    begin
      x = test_vector;
      #10; // Wait for the DUT to process the input
      if (f !== expected_output) begin
        $display("Test failed for input %b, expected %b, got %b", x, expected_output, f);
        failures = failures + 1;
      end
    end
  endtask

  initial begin
    // Test vectors based on the Karnaugh map and don't-care conditions
    // 00 | d
    // 01 | 0
    // 11 | 1
    // 10 | 1
    apply_test(4'b0000, 1'bx); // don't care, can ignore or set to any value

    // 00 | 0
    // 01 | d
    // 11 | 1
    // 10 | 1
    apply_test(4'b0001, 1'b0);

    // 00 | d
    // 01 | 1
    // 11 | d
    // 10 | 0
    apply_test(4'b0011, 1'b1);

    // 00 | d
    // 01 | 0
    // 11 | d
    // 10 | 1
    apply_test(4'b0010, 1'b0);

    // 00 | 1
    // 01 | 1
    // 11 | d
    // 10 | 0
    apply_test(4'b0111, 1'b1);

    // 01 | 1
    // 11 | d
    // 10 | 0
    apply_test(4'b0110, 1'b1);

    // 01 | 0
    // 10 | d
    apply_test(4'b0101, 1'b0);

    // 01 | d
    // 10 | 1
    apply_test(4'b0100, 1'b1);

    // 00 | d
    // 01 | 1
    // 11 | d
    // 10 | 0
    apply_test(4'b1011, 1'b1);

    // 11 | d
    // 10 | 0
    apply_test(4'b1010, 1'b1);

    // 01 | 0
    // 10 | d
    apply_test(4'b1001, 1'b0);

    // 00 | d
    // 10 | 1
    apply_test(4'b1000, 1'b1);

    // Check if all tests passed
    if (failures == 0) begin
      $display("all test cases passed");
    end

    $finish;
  end

endmodule

