module testbench;
    reg [99:0] in;
    wire [99:0] out;
    integer i;
    reg [99:0] expected_out;
    reg error_flag = 0;

    // Instance of the DUT
    top_module UUT (
        .in(in),
        .out(out)
    );

    // Test case generation
    initial begin
        // Test case 1
        in = 100'b0;  // All zeros input
        expected_out = 100'b0;  // All zeros output
        #10;
        if (out != expected_out) error_flag = 1;

        // Test case 2
        in = 100'b1;  // All zeros except LSB
        expected_out = {1'b1, 99'b0};  // All zeros except MSB in output
        #10;
        if (out != expected_out) error_flag = 1;

        // Test case 3
        in = {1'b1, 99'b0};  // All zeros except MSB
        expected_out = 100'b1;  // All zeros except LSB in output
        #10;
        if (out != expected_out) error_flag = 1;

        // Test case 4
        in = ~100'b0;  // All ones input
        expected_out = ~100'b0;  // All ones output
        #10;
        if (out != expected_out) error_flag = 1;

        // Test case 5
        in = 100'b0101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010;
        expected_out = 100'b0101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010;
        #10;
