    // outputs from DUT
    wire walk_left, walk_right, aaah, digging;

    // Instantiate the Device Under Test (DUT)
    top_module dut(
        .clk(clk),
        .areset(areset),
        .bump_left(bump_left),
        .bump_right(bump_right),
        .ground(ground),
        .dig(dig),
        .walk_left(walk_left),
        .walk_right(walk_right),
        .aaah(aaah),
        .digging(digging)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Clock period of 10ns
    end

    // Test cases
    initial begin
        $monitor("At time %t, walk_left = %b, walk_right = %b, aaah = %b, digging = %b", 
                 $time, walk_left, walk_right, aaah, digging);

        // Reset the DUT asynchronously
        areset = 1; #10;
        areset = 0;
        if (walk_left !== 1 || walk_right !== 0 || aaah !== 0 || digging !== 0) $finish;

        // Verify initial walking direction after reset
        // Should start walking left
        ground = 1; bump_left = 0; bump_right = 0; dig = 0;
        #10;
        if (walk_left !== 1 || walk_right !== 0) $finish;

        // Bumped on the right, should walk right
        bump_right = 1; 
        #10;
        bump_right = 0;
        #10;
        if (walk_left !== 0 || walk_right !== 1) $finish;

        // Digging conditions
        ground = 1; dig = 1;
        #10;
        if (digging !== 1 || aaah !== 0) $finish;
        dig = 0;

        // Falling case
        ground = 0;
        #10;
        if (aaah !== 1 || digging !== 0) $finish;

        // Restore ground, resume previous direction
        ground = 1;
        #10;
        if (walk_right !== 1 || aaah !== 0) $finish;

        // Not affected by bumping while digging or falling 
        ground = 1; dig = 1; bump_left = 1;
        #10;
        bump_left = 0;
        #20; // Duration of digging through and subsequently falling
        if (digging !== 1 || walk_left !== 0 || walk_right !== 1) $finish;
        dig = 0;
        ground = 0; // Should now be falling
        #10;
        ground = 1; // Hitting ground
        #10;
        if (walk_left !== 0 || walk_right !== 1) $finish;

        // All test cases passed
        $display("all test cases passed");
    end
endmodule

