    initial begin
        reset = 1; w = 0;
        #40;        // Time for the reset to settle 
        reset = 0;

        // Test sequence according to state diagram

        // A (expect z=0 when moving to B)
        w = 0; #20;
        check(z, 0, "A to B", 1);

        // B (expect z=0 when moving to C)
        w = 0; #20;
        check(z, 0, "B to C", 2);

        // C (expect z=0 when moving to E)
        w = 0; #20;
        check(z, 0, "C to E", 3);

        // E (expect z=1 when staying in E)
        w = 0; #20;
        check(z, 1, "E staying in E", 4);

        w = 1; #20;
        check(z, 1, "E to D", 5);

        // D (expect z=0 when moving to F)
        w = 0; #20;
        check(z, 0, "D to F", 6);

        // F (expect z=1 when moving to C)
        w = 0; #20;
        check(z, 1, "F to C", 7);

        // Checking all the remain possible transitions
        // ...
        // Finish simulation and report
