                $display("Test Case %d (%s) FAILED: Expected '%b', but got '%b'", id, test, expected, val);
            end else begin
                testCasesPassed = testCasesPassed + 1;
            end
        end
    endtask

    // Finish simulation and print results
    always @(posedge clk) begin
        if (testCasesPassed == totalTestCases) begin
            @(negedge clk);
            if (testCasesPassed == totalTestCases) // Final check to make sure no failures afterwards
                $display("all test cases passed");
