module top_module_tb;

    reg in;
    reg [9:0] state;
    wire [9:0] next_state;
    wire out1;
    wire out2;

    // DUT instantiation
    top_module uut (
        .in(in),
        .state(state),
        .next_state(next_state),
        .out1(out1),
        .out2(out2)
    );

    // Initial block - Apply test vectors
    initial begin
        // Display header
        $display("Time\tin\tstate\t->\tnext_state\tout1\tout2");
        $monitor("%g\t%b\t%b\t->\t%b\t%b\t%b", $time, in, state, next_state, out1, out2);

        // Initialize inputs
        state = 10'b0000000001; // Start at state S0
        in = 0;
        #10; // Wait for 10 ns

        // Test State S0 transitions
        in = 1; #10;
        check_transition(10'b0000000010, 0, 0); // S0 to S1 on input 1

        in = 0; #10;
        check_transition(10'b0000000001, 0, 0); // S1 to S0 on input 0

        // Continue with similar tests for each state based on problem description
        // Applying all the state transitions

        in = 1; #10;
        check_transition(10'b0000000100, 0, 0); // S1 to S2

        in = 0; #10;
        check_transition(10'b0000000001, 0, 0); // S2 to S0

        in = 1; #10;
        check_transition(10'b0000001000, 0, 0); // S2 to S3 on input 1

        in = 0; #10;
        check_transition(10'b0000000001, 0, 0); // S3 to S0 on input 0

        in = 1; #10;
        check_transition(10'b0000010000, 0, 0); // S3 to S4

        in = 0; #10;
        check_transition(10'b0000000001, 0, 0); // S4 to S0 on input 0

        in = 1; #10;
        check_transition(10'b0000100000, 0, 0); // S4 to S5

        in = 0; #10;
        check_transition(10'b0100000000, 1, 0); // S5 to S8 on input 0 (out1 should now be 1)

        in = 1; #10;
        check_transition(10'b0000000010, 0, 0); // S8 to S1 on input 1

        in = 0; #10;
        check_transition(10'b1000000000, 1, 1); // S6 to S9 on input 0 (both out1 and out2 should be 1)

        in = 1; #10;
        check_transition(10'b0000000010, 0, 0); // S9 to S1 on input 1

        $finish;
    end  

    // Task to check state transitions and produce output message
    task check_transition;
        input [9:0] expected_state;
        input expected_out1;
        input expected_out2;
        begin
            if (next_state !== expected_state || out1 !== expected_out1 || out2 !== expected_out2) begin
