    top_module DUT (
        .clk(clk),
        .in(in),
        .areset(areset),
        .out(out)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Clock period of 10 ns
    end

    // Test scenarios
    initial begin
        // Initialize inputs
        areset = 1; in = 0;
        #10; // Assert reset
        areset = 0;

        // Test scenario 1: State A to B
        #10; in = 1;
        #10; if (state != B) $display("Test case failed at State A to B.");

        // Test scenario 2: State B to B
        #20; in = 1;
        #10; if (state != B) $display("Test case failed at State B to B.");

        // Test scenario 3: State B to C
        #20; in = 0;
        #10; if (state != C) $display("Test case failed at State B to C.");

        // Test scenario 4: State C to D
        #20; in = 1;
        #10; if (state != D) $display("Test case failed at State C to D.");

        // Test scenario 5: State D to B
        #20; in = 1;
        #10; if (state != B) $display("Test case failed at State D to B.");

        // Test scenario 6: State D to C
        #20; in = 0;
        #10;
        if (state != C) $display("Test case failed at State D to C.");
        else $display("all test cases passed");

        $finish;
    end

    // Monitor state transitions 
    always @(posedge clk) begin
        if (areset) begin
            state <= A;
        end else begin
            case (state)
                A: state <= (in == 0) ? A : B;
                B: state <= (in == 0) ? C : B;
                C: state <= (in == 0) ? A : D;
                D: state <= (in == 0) ? C : B;
            endcase
        end
    end

