module testbench;
    reg clk;
    reg reset;
    reg data;
    reg ack;
    wire [3:0] count;
    wire counting;
    wire done;

    top_module DUT(
        .clk(clk),
        .reset(reset),
        .data(data),
        .count(count),
        .counting(counting),
        .done(done),
        .ack(ack)
    );

    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Clock with period of 10ns
    end

    initial begin
        // Initialize all inputs
        reset = 0;
        data = 0;
        ack = 0;
        #10;

        // Assert reset
        reset = 1;
        #10;
        reset = 0;
        #10;

        // test sequence start
        // Pattern 1101 detection
        {data} = 1; #10;  // bit 1
        {data} = 1; #10;  // bit 1
        {data} = 0; #10;  // bit 0
        {data} = 1; #10;  // bit 1 -> triggered pattern 1101

        // Input 4-bit delay: 0101 = 5 (6000 cycles delay)
        {data} = 0; #10; // MSB
        {data} = 1; #10;
        {data} = 0; #10;
        {data} = 1; #10; // LSB

        // Test counting output
        if (count != 0) begin
            $display("Test Case 1 Failed");
        end
        #5000; // Wait for 5000 cycles

        if (!counting) begin
            $display("Test Case 2 Failed");
        end

        #9990; // run for almost the duration required

        if (!done) begin
            $display("Test Case 3 Failed");
        end

        // Acknowledging the done
        ack = 1; #10;
        ack = 0; #10;

        // Check if system can reset and look for next sequence
        {data} = 1; #10;  // bit 1
        {data} = 1; #10;  // bit 1
        {data} = 0; #10;  // bit 0
        {data} = 1; #10;  // bit 1 -> triggered next pattern 1101

        // Test the output if it does not start another counting till ack
        if (counting) begin
            $display("Test Case 4 Failed");
        end

        // Complete the simulation
        #50; // Some delay before finishing simulation 
        $display("all test cases passed");
        $finish;
    end
