        {x3, x2, x1} = 3'b100;
        #10 if (f !== 0) pass = 0;

        // Test case 6: x3=1, x2=0, x1=1, f should be 1
        {x3, x2, x1} = 3'b101;
        #10 if (f !== 1) pass = 0;

        // Test case 7: x3=1, x2=1, x1=0, f should be 0
        {x3, x2, x1} = 3'b110;
        #10 if (f !== 0) pass = 0;

        // Test case 8: x3=1, x2=1, x1=1, f should be 1
        {x3, x2, x1} = 3'b111;
        #10 if (f !== 1) pass = 0;

        // Check if all test cases passed
        if (pass) $display("all test cases passed");

        // Finish the simulation
        $finish;
    end
endmodule

