module top_module_tb;

    reg in;
    reg [1:0] state;
    wire [1:0] next_state;
    wire out;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .in(in),
        .state(state),
        .next_state(next_state),
        .out(out)
    );

    integer errors = 0;

    initial begin
        // Test Case 1: State A, in = 0
        in = 0; state = 2'b00;
        #10;
        if (next_state !== 2'b00 || out !== 0) begin
            $display("Test Case Failed (State A, in=0)");
            errors = errors + 1;
        end

        // Test Case 2: State A, in = 1
        in = 1; state = 2'b00;
        #10;
        if (next_state !== 2'b01 || out !== 0) begin
            $display("Test Case Failed (State A, in=1)");
            errors = errors + 1;
        end

        // Test Case 3: State B, in = 0
        in = 0; state = 2'b01;
        #10;
        if (next_state !== 2'b10 || out !== 0) begin
            $display("Test Case Failed (State B, in=0)");
            errors = errors + 1;
        end

        // Test Case 4: State B, in = 1
        in = 1; state = 2'b01;
        #10;
        if (next_state !== 2'b01 || out !== 0) begin
            $display("Test Case Failed (State B, in=1)");
            errors = errors + 1;
        end

        // Test Case 5: State C, in = 0
        in = 0; state = 2'b10;
        #10;
        if (next_state !== 2'b00 || out !== 0) begin
            $display("Test Case Failed (State C, in=0)");
            errors = errors + 1;
        end

        // Test Case 6: State C, in = 1
        in = 1; state = 2'b10;
        #10;
        if (next_state !== 2'b11 || out !== 0) begin
            $display("Test Case Failed (State C, in=1)");
            errors = errors + 1;
        end

        // Test Case 7: State D, in = 0
        in = 0; state = 2'b11;
        #10;
        if (next_state !== 2'b10 || out !== 1) begin
            $display("Test Case Failed (State D, in=0)");
            errors = errors + 1;
        end

        // Test Case 8: State D, in = 1
        in = 1; state = 2'b11;
        #10;
        if (next_state !== 2'b01 || out !== 1) begin
            $display("Test Case Failed (State D, in=1)");
            errors = errors + 1;
        end

        if (errors == 0) begin
            $display("all test cases passed");
        end

        $finish;
    end

endmodule

