module testbench;
    reg [1:0] A;
    reg [1:0] B;
    wire z;

    top_module uut (
        .A(A), 
        .B(B), 
        .z(z)
    );

    initial begin
        // Case 1: A and B are equal
        A = 2'b00; B = 2'b00; #10;
        if (z !== 1'b1) failTest();

        A = 2'b01; B = 2'b01; #10;
        if (z !== 1'b1) failTest();

        A = 2'b10; B = 2'b10; #10;
        if (z !== 1'b1) failTest();

        A = 2'b11; B = 2'b11; #10;
        if (z !== 1'b1) failTest();

        // Case 2: A and B are not equal
        A = 2'b00; B = 2'b01; #10;
        if (z !== 1'b0) failTest();

        A = 2'b01; B = 2'b10; #10;
        if (z !== 1'b0) failTest();

        A = 2'b10; B = 2'b11; #10;
        if (z !== 1'b0) failTest();

        A = 2'b11; B = 2'b00; #10;
        if (z !== 1'b0) failTest();

        // If all tests pass
        $display("all test cases passed");
        $finish;
    end

    task failTest;
        begin
            $display("Test case failed at A=%b, B=%b", A, B);
            $finish;
        end
    endtask
endmodule

