module tb_top_module;

    reg clk;
    reg a;
    reg b;
    wire out_assign;
    wire out_always_comb;
    reg out_always_ff;    // Changed from wire to reg due to feedback flip flop loop

    top_module dut (
        .clk(clk),
        .a(a),
        .b(b),
        .out_assign(out_assign),
        .out_always_comb(out_always_comb),
        .out_always_ff(out_always_ff)
    );

    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // Clock with period of 10 ns
    end

    integer errors = 0;

    initial begin
        // Test Case 1: a = 0, b = 0
        a = 0; b = 0;
        #10;  // Wait for the outputs to update, including potential FF delay
        if (out_assign !== (a ^ b)) errors = errors + 1;
        if (out_always_comb !== (a ^ b)) errors = errors + 1;
        #10;  // Wait another cycle for flip flop output
        if (out_always_ff !== (a ^ b)) errors = errors + 1;

        // Test Case 2: a = 0, b = 1
        a = 0; b = 1;
        #10;
        if (out_assign !== (a ^ b)) errors = errors + 1;
        if (out_always_comb !== (a ^ b)) errors = errors + 1;
        #10;
        if (out_always_ff !== (a ^ b)) errors = errors + 1;

        // Test Case 3: a = 1, b = 0
        a = 1; b = 0;
        #10;
        if (out_assign !== (a ^ b)) errors = errors + 1;
        if (out_always_comb !== (a ^ b)) errors = errors + 1;
        #10;
        if (out_always_ff !== (a ^ b)) errors = errors + 1;

        // Test Case 4: a = 1, b = 1
        a = 1; b = 1;
        #10;
        if (out_assign !== (a ^ b)) errors = errors + 1;
        if (out_always_comb !== (a ^ b)) errors = errors + 1;
        #10;
        if (out_always_ff !== (a ^ b)) errors = errors + 1;

        if (errors == 0) begin
            $display("all test cases passed");
        end

        $finish;
    end

endmodule

