        reset = 1;
        j = 0;
        k = 0;

        // Reset the state machine
        #10 reset = 0;
        #10 reset = 1;
        #10 reset = 0;

        // TestCase 1: OFF -> OFF (j = 0)
        j = 0;
        k = 0; // k shouldn't matter in state OFF
        #10;
        if (out !== 0) $display("Test Case 1 Failed: Expect OFF (out=0)");

        // TestCase 2: OFF -> ON (j = 1)
        j = 1;
        #10;
        if (out !== 1) $display("Test Case 2 Failed: Expect ON (out=1)");

        // TestCase 3: ON -> ON (k = 0)
        k = 0;
        #10;
        if (out !== 1) $display("Test Case 3 Failed: Expect ON (out=1) when k=0");

        // TestCase 4: ON -> OFF (k = 1)
        k = 1;
        #10;
        if (out !== 0) $display("Test Case 4 Failed: Expect OFF (out=0) when k=1");

        // Check if all test cases passed
        if (!$isunknown(out) && !$countdrivers(out)) begin
            $display("All test cases passed successfully");
        end

