module testbench;
    // Signal Declarations
    reg clk;
    reg reset;
    wire [31:0] q;

    // Variables for LFSR verification
    reg [31:0] expected_lfsr_value; 
    integer i;

    // Instance of the DUT
    top_module DUT (
        .clk(clk),
        .reset(reset),
        .q(q)
    );

    // Clock generation
    always begin
        clk = 1; #5; clk = 0; #5;  // Generate a clock with 10ns period
    end

    // Test Cases
    initial begin
        // Test Case 1: Check reset functionality
        reset = 1; #10; // Assert reset
        if (q !== 32'h1) begin
            $display("Reset test failed. q=%h, expected=32'h1", q);
            $finish;
        end
        reset = 0;  // Deassert reset

        // Generate the expected LFSR sequence
        expected_lfsr_value = 32'h1;  // Initial state after reset

        // Test Case 2: Check LFSR correctness for several cycles
        for (i = 0; i < 100; i = i + 1) begin
            #10; // wait for one clock cycle
            expected_lfsr_value = {expected_lfsr_value[30:0], expected_lfsr_value[31] ^ expected_lfsr_value[21] ^ expected_lfsr_value[1] ^ expected_lfsr_value[0]};
            if (q !== expected_lfsr_value) begin
                $display("LFSR behavior test failed at cycle %d: q=%h, expected=%h", i, q, expected_lfsr_value);
                $finish;
            end
        end

        // If all tests pass    
        $display("all test cases passed");
        $finish;
    end

endmodule

