module tb_top_module;

    // Inputs
    reg clk;
    reg areset;
    reg bump_left;
    reg bump_right;
    reg ground;
    reg dig;

    // Outputs
    wire walk_left;
    wire walk_right;
    wire aaah;
    wire digging;

    // Instantiate the Device Under Test (DUT)
    top_module dut(
        .clk(clk),
        .areset(areset),
        .bump_left(bump_left),
        .bump_right(bump_right),
        .ground(ground),
        .dig(dig),
        .walk_left(walk_left),
        .walk_right(walk_right),
        .aaah(aaah),
        .digging(digging)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // 100MHz Clock
    end

    // Test cases
    integer errors = 0;

    initial begin
        // Initialize Inputs
        areset = 1;
        bump_left = 0;
        bump_right = 0;
        ground = 1;
        dig = 0;

        // Wait for reset to take effect
        #10;
        areset = 0;

        // Check initial state (should be walking left)
        if (!walk_left || walk_right || aaah || digging) begin
            $display("Test Case 1 Failed: Initial state error.");
            errors = errors + 1;
        end

        // Test Case 2: Bump on the left
        #10 bump_left = 1;
        #10 bump_left = 0;
        if (!walk_right || walk_left || aaah || digging) begin
            $display("Test Case 2 Failed: Bump on the left error.");
            errors = errors + 1;
        end

        // Test Case 3: Hit obstacle on the right
        #10 bump_right = 1;
        #10 bump_right = 0;
        if (!walk_left || walk_right || aaah || digging) begin
            $display("Test Case 3 Failed: Bump on the right error.");
            errors = errors + 1;
        end

        // Test Case 4: Ground disappears
        #10 ground = 0;
        #20;
        if (!aaah || walk_left || walk_right || digging) begin
            $display("Test Case 4 Failed: Ground disappears error.");
            errors = errors + 1;
        end

        // Test Case 5: Continuous fall and splatter
        #410; // Falling more than 20 cycles
        ground = 1;
        #10;
        if (walk_left || walk_right || aaah || digging) begin
            $display("Test Case 5 Failed: Splatter error.");
            errors = errors + 1;
        end

        // All test cases passed check
        #10; // Wait for all tests to complete evaluation
        if (errors == 0) begin
            $display("all test cases passed");
        end

        $finish;
    end

endmodule
