	reg state;
	reg next;

    always_comb begin
		case (state)
			A: next = in ? A : B;
			B: next = in ? B : A;
		endcase
    end

    always @(posedge clk) begin
		if (reset) state <= B;
        else state <= next;
	end

	assign out = (state==B);


endmodule


